import React, { useState } from 'react';
import axios from 'axios';
import { Send, Bot, Zap, MessageCircle } from 'lucide-react';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('satirical-bot');
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!message.trim()) return;

    setLoading(true);
    setError(null);
    setResponse(null);

    try {
      let endpoint = '';
      switch (activeTab) {
        case 'satirical-bot':
          endpoint = '/api/satirical-bot';
          break;
        case 'satirical-bot-1':
          endpoint = '/api/satirical-bot-1';
          break;
        case 'lambda':
          endpoint = '/api/lambda';
          break;
        default:
          endpoint = '/api/satirical-bot';
      }

      const config = {
        method: activeTab === 'lambda' ? 'get' : 'post',
        url: endpoint,
        ...(activeTab !== 'lambda' && { data: { message } })
      };

      const result = await axios(config);
      setResponse(result.data);
    } catch (err) {
      setError(err.response?.data?.error || err.message || 'Something went wrong');
    } finally {
      setLoading(false);
    }
  };

  const renderResponse = () => {
    if (!response) return null;

    return (
      <div className="response-container">
        <h3>ü§ñ AI Response</h3>
        {response.output && (
          <>
            {response.output.textbox_value && (
              <div className="response-box">
                <strong>Text Response:</strong>
                <div>{response.output.textbox_value}</div>
              </div>
            )}
            {response.output.chatbot_value && (
              <div className="response-box">
                <strong>Chat Response:</strong>
                <div>{response.output.chatbot_value}</div>
              </div>
            )}
          </>
        )}
        {response.response && (
          <div className="response-box">
            <strong>Response:</strong>
            <div>{JSON.stringify(response.response, null, 2)}</div>
          </div>
        )}
        <div className="success">
          ‚úÖ Request completed successfully at {new Date(response.timestamp).toLocaleTimeString()}
        </div>
      </div>
    );
  };

  return (
    <div className="App">
      <div className="container">
        <div className="card">
          <h1>ü§ñ Unmaad Satirical Bot</h1>
          <p>AI-powered satirical responses powered by Gradio</p>
        </div>

        <div className="card">
          <div className="tabs">
            <button
              className={`tab ${activeTab === 'satirical-bot' ? 'active' : ''}`}
              onClick={() => setActiveTab('satirical-bot')}
            >
              <Bot size={16} />
              Satirical Bot
            </button>
            <button
              className={`tab ${activeTab === 'satirical-bot-1' ? 'active' : ''}`}
              onClick={() => setActiveTab('satirical-bot-1')}
            >
              <MessageCircle size={16} />
              Satirical Bot 1
            </button>
            <button
              className={`tab ${activeTab === 'lambda' ? 'active' : ''}`}
              onClick={() => setActiveTab('lambda')}
            >
              <Zap size={16} />
              Lambda
            </button>
          </div>

          <form onSubmit={handleSubmit}>
            {activeTab !== 'lambda' && (
              <textarea
                className="input"
                placeholder="Enter your message here..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                rows={4}
                disabled={loading}
              />
            )}

            <button
              type="submit"
              className="btn"
              disabled={loading || (activeTab !== 'lambda' && !message.trim())}
            >
              {loading ? (
                <>
                  <div className="loading-spinner"></div>
                  Processing...
                </>
              ) : (
                <>
                  <Send size={16} />
                  {activeTab === 'lambda' ? 'Call Lambda' : 'Send Message'}
                </>
              )}
            </button>
          </form>

          {error && (
            <div className="error">
              ‚ùå Error: {error}
            </div>
          )}

          {renderResponse()}
        </div>

        <div className="card">
          <h3>üìã API Information</h3>
          <div className="api-info">
            <p><strong>Current Endpoint:</strong> {activeTab}</p>
            <p><strong>Method:</strong> {activeTab === 'lambda' ? 'GET' : 'POST'}</p>
            <p><strong>Parameters:</strong> {activeTab === 'lambda' ? 'None' : 'message (string)'}</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
